mapper.insert:
INSERT INTO account(acct_number
                    ,r_num
                    ,acct_balance
                    ,acct_dealdate
                    ,trade_ammount
                    ,trade
                    ,trade_detail
                    ,trade_target
                    ,mem_id
                    ,hidden)
             VALUES((SELECT max(acct_number) FROM account
                WHERE mem_id = #{mem_id})
                    ,(SELECT max(r_num)+1 FROM account WHERE mem_id=#{mem_id})
                    ,#{acct_balance}
                    ,(SELECT TO_CHAR(SYSDATE, 'YYYY/MM/DD/HH/MI/SS') FROM DUAL)
                    ,#{trade_ammount}
                    ,#{trade}
                    ,#{trade_detail}
                    ,#{trade_target}
                    ,#{mem_id}
                    ,'no'
                    )

mapper.delete:
UPDATE account
   SET hidden='yes'
 WHERE mem_id=#{mem_id}
   AND r_num = #{r_num}
