mapper.select:
	SELECT bidders_id 
	  FROM seed 
     WHERE bid_code=#{bid_code}

mapper.insert:
UPDATE seed
   SET outcome ='낙찰'
 WHERE bidders_id=#{bidders_id}
   AND bid_code=#{bid_code}

UPDATE seed
   SET outcome ='패찰'
 WHERE bid_code=#{bid_code}

mapper.select:
  		SELECT nvl((SELECT bidders_id from seed 
			WHERE bid_code=#{bid_code}
			AND bidders_id=#{bidders_id}),0) FROM dual
